mapper.select:
		SELECT
			*
		FROM
			scd_code
		WHERE
			1=1
		
 	        AND code_div =  #{code_div}
 	    
 	    
 	        AND code =  #{code}
 	    
 	    
 	        AND code_name like % #{code_name} % 
 	    
 	    
 	        AND use_method1 =  #{use_method1}
 	    
 	    
 	        AND use_method2 =  #{use_method2}
 	    
 	    
 	        AND use_method3 =  #{use_method3}
 	    
 	    
 	        AND use_yn =  #{use_yn}
 	    
 	    
 	    ORDER BY order_seq ASC

    
        SELECT
			*
		FROM
			scd_code
		WHERE
			1=1
		
 	        AND code_div =  #{code_div}
 	    
 	    
 	        AND code =  #{code}
 	    
 	    
 	        AND code_name like % #{code_name} % 
 	    
 	    
 	        AND use_method1 =  #{use_method1}
 	    
 	    
 	        AND use_method2 =  #{use_method2}
 	    
 	    
 	        AND use_method3 =  #{use_method3}
 	    
 	    
 	        AND use_yn =  #{use_yn}
 	    
 	    
 	    ORDER BY order_seq ASC
